  upload.single("picture"),
  isValid("blog"),
  postBlog
);
router.get("/:id", getSingleBlog);

router.patch(
  "/:id",
  isAuth(passport),
  upload.single("picture"),
  isValid("blog"),
  updateBlog
);
router.delete("/:id", isAuth(passport), deleteBlog);

export default router;

